mapper.select:
SELECT DISTINCT order.id,createDate,`number`,productpic.picPath,product.descript,sku.price,skuCon,OrderStatus, order.orderNumber,order.order_price,order_status.status,productName AS order3 FROM ageless.order INNER JOIN order_status ON ageless.order.OrderStatus=order_status.id INNER JOIN product ON ageless.order.productid=product.id INNER JOIN ageless.order_details ON ageless.order_details.oid=order.id INNER JOIN productpic ON ageless.order.productid=productpic.productId INNER JOIN sku ON sku.SKUId=order_details.skuId LEFT JOIN skuoption ON skuoption.productId=order_details.shipid WHERE 1=1
and order_status.status=#{status}
GROUP BY order.id

mapper.update:
UPDATE ageless.order SET
OrderStatus = #{OrderStatus}
WHERE  id =#{id}
