mapper.select:
SELECT DISTINCT order.id,createDate,`number`,productpic.picPath,product.descript,product.price,order.orderNumber,order.order_price,order_status.status FROM ageless.order INNER JOIN order_status ON ageless.order.OrderStatus=order_status.id INNER JOIN product ON ageless.order.productid=product.id INNER JOIN productpic ON ageless.order.productid=productpic.productId WHERE 1=1
and order_status.status=#{status}
and order.id=#{id}
